import{_ as i,o as l,c as t,a as e}from"./app.d789413f.js";const r=JSON.parse('{"title":"코드앤버터 소개","description":"","frontmatter":{"head":[["meta",{"property":"og:url","content":"https://docs.codenbutter.com/guide/intro.html"}],["meta",{"name":"twitter:url","content":"https://docs.codenbutter.com/guide/intro.html"}],["meta",{"property":"og:description","content":"코드앤버터 소개"}],["meta",{"name":"twitter:description","content":"코드앤버터 소개"}]]},"headers":[{"level":2,"title":"주요기능","slug":"주요기능","link":"#주요기능","children":[]},{"level":2,"title":"앞으로","slug":"앞으로","link":"#앞으로","children":[]},{"level":2,"title":"더 알아보기","slug":"더-알아보기","link":"#더-알아보기","children":[]}],"relativePath":"guide/intro.md","lastUpdated":1663207341000}'),a={name:"guide/intro.md"},n=[e('<h1 id="코드앤버터-소개" tabindex="-1">코드앤버터 소개 <a class="header-anchor" href="#코드앤버터-소개" aria-hidden="true">#</a></h1><div class="no-shadow"><p><img src="/assets/popup.2aec7195.png" alt="추석 팝업"></p></div><p><strong>웹사이트를 꾸미는 가장 쉬운 방법</strong> 🧈 코드앤버터</p><p>코드앤버터는 &quot;<u>누구나 쉽고 예쁘게 사이트를 꾸밀 수 있도록 도와주는 서비스</u>&quot;입니다.</p><ul><li><strong>누구나</strong> - 코딩 경험이 없어도</li><li><strong>쉽고</strong> - 스크립트 한줄을 추가하는 것으로</li><li><strong>예쁘게</strong> - 템플릿과 위지윅 에디터 커스터마이징을 통해</li><li><strong>사이트를 꾸미자</strong> - 사이트에 필요한 여러가지 기능을 추가한다!</li></ul><p>모든 기능을 직접 만들지 말고, 가져다 사용하세요. 플랫폼의 제한된 기능을 손쉽게 확장하세요.</p><p>팝업 컴포넌트를 시작으로 배너, 컨텐츠 상세, 공지사항, 게시판, FAQ등 꼭 필요하지만 개발하기 귀찮은 작업을 추가하고 있습니다. 🔥</p><h2 id="주요기능" tabindex="-1">주요기능 <a class="header-anchor" href="#주요기능" aria-hidden="true">#</a></h2><ul><li><strong>🪄 NOCODE</strong> 코드를 몰라도 원하는 템플릿을 선택하고 드래그앤드롭으로 팝업을 만들 수 있습니다.</li><li><strong>🎨 CUSTOMIZING</strong> 팝업의 위치, 크기, 색상, 내용, 노출 시간, 노출 페이지등을 설정할 수 있습니다.</li><li><strong>😀 FLEXIBILITY</strong> 스크립트를 추가하는 방식으로 대부분의 라이브러리, 프레임워크, 서비스에 손쉽게 사용할 수 있습니다.</li><li><strong>📈 OPTIMIZE</strong> 페이지뷰, 세션뷰와 같은 통계를 확인할 수 있습니다.</li><li>👋 그 외에 필요한 기능이 있으면 요청해주세요. 저희가 대신 만들어 드립니다. 😃</li></ul><h2 id="앞으로" tabindex="-1">앞으로 <a class="header-anchor" href="#앞으로" aria-hidden="true">#</a></h2><p>팝업을 시작으로 앞으로 계획은 다음과 같습니다.</p><ul><li>피드백 개선 개선 개선!</li><li>플랫폼 자동 연동 지원</li><li>더 많은 컴포넌트 지원</li><li>관리, 디자인, 편집 권한 추가</li><li>Headless CMS 연동</li><li>React.js/Vue.js 라이브러리 제공</li><li>템플릿/컴포넌트를 사고 팔 수 있는 스토어 개발</li><li>마케팅, 퍼포먼스 분석 기능 추가</li><li>필요한 컴포넌트를 더 쉽고 빠르게 만들 수 있는 AI / 자동 최적화 기능 강화</li><li>엔터프라이즈 수준 보안 강화 (2차 인증, SAML 연동, ...)</li><li>오픈 API / Webhook 제공</li><li>실시간 협업 기능 추가</li><li>...? 😎</li></ul><h2 id="더-알아보기" tabindex="-1">더 알아보기 <a class="header-anchor" href="#더-알아보기" aria-hidden="true">#</a></h2><ul><li><a href="./vs-other-apps.html">다른 도구와 비교하기</a></li><li><a href="./basic.html">팝업 만들기 가이드 보기</a></li><li><a href="./faq.html">자주 묻는 질문 안내</a></li></ul>',14)];const s=i(a,[["render",function(i,e,r,a,s,o){return l(),t("div",null,n)}]]);export{r as __pageData,s as default};
